INSERT INTO `blogs` VALUES ('35', 'Python List数据的遍历', '34', '', '25', '<h4 id=\"h4--\"><a name=\"方式一：\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>方式一：</h4><pre class=\"prettyprint linenums prettyprinted\"><ol class=\"linenums\"><li class=\"L0\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L1\"><code class=\"lang-python\"><span class=\"pln\">app_list </span><span class=\"pun\">=</span><span class=\"pln\"> </span><span class=\"pun\">[</span><span class=\"lit\">1234</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">8899</span><span class=\"pun\">]</span></code></li><li class=\"L2\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L3\"><code class=\"lang-python\"><span class=\"kwd\">for</span><span class=\"pln\"> app_id </span><span class=\"kwd\">in</span><span class=\"pln\"> app_list</span><span class=\"pun\">:</span></code></li><li class=\"L4\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L5\"><code class=\"lang-python\"><span class=\"pln\">    </span><span class=\"kwd\">print</span><span class=\"pln\"> app_id</span></code></li><li class=\"L6\"><code class=\"lang-python\"><span class=\"pun\">输出：</span><span class=\"pln\"> </span><span class=\"lit\">1234</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pln\"> </span><span class=\"lit\">8899</span></code></li></ol></pre>\n<h4 id=\"h4--\"><a name=\"方式二：\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>方式二：</h4><pre class=\"prettyprint linenums prettyprinted\"><ol class=\"linenums\"><li class=\"L0\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L1\"><code class=\"lang-python\"><span class=\"pln\">app_list </span><span class=\"pun\">=</span><span class=\"pln\"> </span><span class=\"pun\">[</span><span class=\"lit\">1234</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">8899</span><span class=\"pun\">]</span></code></li><li class=\"L2\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L3\"><code class=\"lang-python\"><span class=\"kwd\">for</span><span class=\"pln\"> index</span><span class=\"pun\">,</span><span class=\"pln\">app_id </span><span class=\"kwd\">in</span><span class=\"pln\"> enumerate</span><span class=\"pun\">(</span><span class=\"pln\">app_list</span><span class=\"pun\">):</span></code></li><li class=\"L4\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L5\"><code class=\"lang-python\"><span class=\"pln\">    </span><span class=\"kwd\">print</span><span class=\"pln\"> index</span><span class=\"pun\">,</span><span class=\"pln\"> app_id</span></code></li><li class=\"L6\"><code class=\"lang-python\"><span class=\"pun\">输出：</span><span class=\"pln\"> </span><span class=\"lit\">0</span><span class=\"pln\"> </span><span class=\"lit\">1234</span><span class=\"pln\"> </span><span class=\"lit\">1</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pln\"> </span><span class=\"lit\">2</span><span class=\"pln\"> </span><span class=\"lit\">8899</span></code></li></ol></pre>\n<h4 id=\"h4--range-xrange-\"><a name=\"方式三： 使用range()或xrange()\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>方式三： 使用range()或xrange()</h4><pre class=\"prettyprint linenums prettyprinted\"><ol class=\"linenums\"><li class=\"L0\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L1\"><code class=\"lang-python\"><span class=\"pln\"> app_list </span><span class=\"pun\">=</span><span class=\"pln\"> </span><span class=\"pun\">[</span><span class=\"lit\">1234</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">8899</span><span class=\"pun\">]</span></code></li><li class=\"L2\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L3\"><code class=\"lang-python\"><span class=\"pln\">    </span><span class=\"kwd\">for</span><span class=\"pln\"> i </span><span class=\"kwd\">in</span><span class=\"pln\"> range</span><span class=\"pun\">(</span><span class=\"pln\">len</span><span class=\"pun\">(</span><span class=\"pln\">app_list</span><span class=\"pun\">)):</span></code></li><li class=\"L4\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L5\"><code class=\"lang-python\"><span class=\"pln\">          </span><span class=\"kwd\">print</span><span class=\"pln\"> i</span><span class=\"pun\">,</span><span class=\"pln\">app_list</span><span class=\"pun\">[</span><span class=\"pln\">i</span><span class=\"pun\">]</span></code></li><li class=\"L6\"><code class=\"lang-python\"><span class=\"pun\">输出：</span><span class=\"pln\"> </span><span class=\"lit\">0</span><span class=\"pln\"> </span><span class=\"lit\">1234</span><span class=\"pln\"> </span><span class=\"lit\">1</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pln\"> </span><span class=\"lit\">2</span><span class=\"pln\"> </span><span class=\"lit\">8899</span></code></li></ol></pre>\n<h4 id=\"h4--iter-\"><a name=\"方式四： 使用iter()\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>方式四： 使用iter()</h4><pre class=\"prettyprint linenums prettyprinted\"><ol class=\"linenums\"><li class=\"L0\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L1\"><code class=\"lang-python\"><span class=\"pln\">app_list </span><span class=\"pun\">=</span><span class=\"pln\"> </span><span class=\"pun\">[</span><span class=\"lit\">1234</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pun\">,</span><span class=\"pln\"> </span><span class=\"lit\">8899</span><span class=\"pun\">]</span></code></li><li class=\"L2\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L3\"><code class=\"lang-python\"><span class=\"kwd\">for</span><span class=\"pln\"> app_id </span><span class=\"kwd\">in</span><span class=\"pln\"> iter</span><span class=\"pun\">(</span><span class=\"pln\">app_list</span><span class=\"pun\">):</span></code></li><li class=\"L4\"><code class=\"lang-python\"><span class=\"pun\">&lt;!--</span><span class=\"pln\"> lang</span><span class=\"pun\">:</span><span class=\"pln\"> python </span><span class=\"pun\">--&gt;</span></code></li><li class=\"L5\"><code class=\"lang-python\"><span class=\"pln\">    </span><span class=\"kwd\">print</span><span class=\"pln\"> app_id</span></code></li><li class=\"L6\"><code class=\"lang-python\"><span class=\"pun\">输出：</span><span class=\"pln\"> </span><span class=\"lit\">1234</span><span class=\"pln\"> </span><span class=\"lit\">5677</span><span class=\"pln\"> </span><span class=\"lit\">8899</span></code></li></ol></pre>\n', '2016-09-20 16:29:14');
