DROP TABLE IF EXISTS `comments`;
CREATE TABLE `comments` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `blog_id` int(10) NOT NULL,
  `from_user_id` varchar(32) NOT NULL,
  `from_user_name` varchar(50) NOT NULL,
  `from_user_logo` varchar(500) NOT NULL,
  `from_user_email` varchar(255) DEFAULT NULL,
  `to_user_id` varchar(32) DEFAULT NULL,
  `to_user_logo` varchar(255) DEFAULT NULL,
  `to_user_name` varchar(255) DEFAULT NULL,
  `to_user_email` varchar(255) DEFAULT NULL,
  `content` mediumtext NOT NULL,
  `create_time` datetime NOT NULL,
  `display` tinyint(3) unsigned DEFAULT '1' COMMENT '默认显示1，不显示则为删除0',
  `parent` int(10) unsigned DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `idx_created_at` (`create_time`)
) ENGINE=MyISAM AUTO_INCREMENT=30 DEFAULT CHARSET=utf8;

